using UnityEngine;using System;using System.Collections.Generic;public class GameManager{	static public GameManager instance;	public static void Init() {instance = new GameManager();}	public List<Player> players = new List<Player>();	public List<Player> activePlayers = null;	public List<PlayerController> availablePlayerControllers = new List<PlayerController>();		public GameManager()	{		players.Add(new Player(0, "PURPLE PLAYER", RXColor.GetColorFromHex(0xFF00EE)));		players.Add(new Player(1, "GREEN PLAYER", RXColor.GetColorFromHex(0x00FF00)));		players.Add(new Player(2, "BLUE PLAYER", RXColor.GetColorFromHex(0x0011EE)));		players.Add(new Player(3, "RED PLAYER", RXColor.GetColorFromHex(0xFF0011)));		availablePlayerControllers.Add(new KeyboardPlayerController(false));		availablePlayerControllers.Add(new KeyboardPlayerController(true));	}	public void SetRoundData(List<Player> activePlayers)	{		this.activePlayers = activePlayers;	}	public void Reset()	{		foreach (Player player in players)		{			player.Reset();		}	}}